loadingDetails=true;
K('bridgedResource[Get Member]').load({
  attributes: ['Member ID', 'First Name', 'Last Name'],
  values: {'ID' : 'Allen'},
  success: function(bridgedData) {
    K('field[Member ID]').value(bridgedData['Member ID']);
    K('field[Student First Name]').value(bridgedData['First Name']);
    K('field[Student Last Name]').value(bridgedData['Last Name']);
    K('field[Billing Customer Reference]').value(bridgedData['Billing Customer Reference']);
    K('field[Membership Cost]').value(bridgedData['Membership Cost']);
    K('field[Payment Frequency]').value(bridgedData['Billing Period']);
    K('field[Billing Period]').value(bridgedData['Billing Period']);
    K('field[Billing Start Date]').value(bridgedData['Billing Start Date']);
    K('field[Non Paying]').value(bridgedData['Non Paying']);
    K('field[Billing Parent Member]').value(bridgedData['Billing Parent Member']);
    K('field[Billing Family Members]').value(bridgedData['Billing Family Members']);

    var paymentPeriod=bridgedData['Billing Period'];
    var period="months";
    if (paymentPeriod==="Daily") {
      period="days";
    } else if (paymentPeriod==="Weekly" || paymentPeriod==="Fortnightly") {
      period="weeks"; 
    } else if (paymentPeriod==="Monthly") {
      period="months"; 
    } 
    debugger;
    var nextBillingDate=undefined;
    if (moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").isAfter(moment())||moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").isSame(moment().hour(0).minute(0).second(0).millisecond(0))){
      nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD");
    } else {
      nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").add(moment().diff(moment(bridgedData['Billing Start Date'],"YYYY-MM-DD"), period)+1,period);
      if (paymentPeriod==="Fortnightly") {
        days=moment().diff(moment(bridgedData['Billing Start Date'],"YYYY-MM-DD"), "days");
        rem=days%14;
        days=days-rem+14;
        nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").add(days, "days");
      }
    }

    K('field[Billing Next Date]').value(nextBillingDate.format('YYYY-MM-DD'));    
    K('field[Date payment will resume]').value(nextBillingDate.format('YYYY-MM-DD'));

    loadingDetails=false;

    var schoolLegalName=K('space').attributes('School Legal Name');
    var schoolEmail=K('space').attributes('School Email');

    var html=$("#termsInformation").html();
    html=html.replaceAll("$school_legal_name$", schoolLegalName);
    html=html.replaceAll("$school_email$", schoolEmail);
    $("#termsInformation").html(html);

    html=primaryHTML.replaceAll("$studentName$", K('field[Student First Name]').value()+" "+K('field[Student Last Name]').value());
    html=html.replaceAll("$parentMemberID$", bridgedData['Billing Parent Member']);    
		$(K('content[Primary Family Member Info]').element()).html(html);    

    html=dependantHTML.replaceAll("$studentName$", K('field[Student First Name]').value()+" "+K('field[Student Last Name]').value());
    html=html.replaceAll("$memberGUID$", bridgedData['id']);    
    html=html.replaceAll("$parentMemberID$", bridgedData['Billing Parent Member']);    
		$(K('content[Family Dependant Info]').element()).html(html);


    html=nonpayingHTML.replaceAll("$studentName$", K('field[Student First Name]').value()+" "+K('field[Student Last Name]').value());
    html=html.replaceAll("$memberGUID$", bridgedData['id']);    
		$(K('content[Non Paying Info]').element()).html(html);

    K('section[Member Select Space]').hide();    
    }
});

K('field[Member ID]').value("");
K('field[Student First Name]').value("");
K('field[Student Last Name]').value("");
K('field[Billing Customer Reference]').value("");
K('field[Membership Cost]').value("");

K('section[Student Information]').hide();
K('section[Cancellation Information]').hide();
K('section[Terms]').hide();
K('section[List of Documents]').hide();    

K('section[Member Select Space]').show();

bundle.config.widgets.signatureCanvas({
  element: K("form").find("#member-signature-canvas")[0],
  initialValue: K("field[Encoded Member Signature]").value(),
  onChange: function(value) {
	  K("field[Encoded Member Signature]").value(value);
	},
  height: 250,
  width: 500,
  disable: form('review'),
  ref: function(el) {
    window.memberSignaturePad = el;
  },
});

bundle.config.widgets.selectMenu({
  element: K("form").find("#selectMemberMenu")[0],
	options: K('field[Members]').options(),
  onChange: function(option) {
	  K('field[Members]').value(option.value);
    setTimeout(function(){
	    $(K("form").find("#selectMemberMenu .css-1492t68")).html(option.label);
    },100);
	},
  value: K('field[Members]').value(),
});

if (GetURLParameter("id")!==null) {
  K('field[Members]').value(GetURLParameter("id"));
  $(K('field[Members]').options()).each(function(index) {
    if (K('field[Members]').options()[index].value===GetURLParameter("id")) {
      label=K('field[Members]').options()[index].label;
    	$(K("form").find("#selectMemberMenu [class$='placeholder']")).html(label);
    }
  });      
}

K('section[Student Information]').show(); 
K('section[Terms]').show();

K('section[Member Select Space]').hide();

if (K('field[Members]').value()===null) {
	loadingDetails=true;
  K('section[Member Select Space]').show();   
}else {
	loadingDetails=false;
  K('section[Member Select Space]').hide();   
}

primaryHTML=$(K('content[Primary Family Member Info]').element()).html();
dependantHTML=$(K('content[Family Dependant Info]').element()).html();
nonpayingHTML=$(K('content[Non Paying Info]').element()).html();
